package days.year2022

import days.Day


fun main(args: Array<String>) {
    println(Day6().solve())
}


class Day6: Day(6) {

    private val input = ""
    private val l = 14

    fun solve(): Any {
        return input
            .forEachIndexed{i,it->
                if(input.substring(i,i+l).groupingBy { it }.eachCount().size==l){
                    return i +14
                }
            }
    }
}

